mapper.update:
	    UPDATE TB_STO_APP_VER SET
  			BIN_VER = #{BIN_VER}
  			, MOD_ID = #{MOD_ID}
  			, MOD_DTTM = SYSDATE
  		WHERE 
  			APP_VER_ID = #{APP_VER_ID}

mapper.insert:
	    INSERT INTO TB_STO_APP_VER 
	    ( 
	    	  APP_VER_ID
	    	, SVC_ID
  			, BIN_VER
  			, PLAT_IDX 
  			, REG_DTTM
  			, REG_ID
  			, DOWNLOAD_MARKET_URL
  			, MEMO 
		) VALUES ( 
			(SELECT IFNULL(MAX(CAST(APP_VER_ID AS INT)),0) + 1 FROM TB_STO_APP_VER)
	    	, #{SVC_ID}
  			, #{BIN_VER}
  			, #{PLAT_IDX}
  			, SYSDATE
  			, #{REG_ID}
  			, #{DOWNLOAD_MARKET_URL}
  			, #{MEMO}
  		)
