SELECT  A.* 
,       (SELECT COUNT(1) FROM LST) TOT_CNT
FROM    LST A
WHERE   RNUM    BETWEEN #{PAGE_SIZE} * (#{PAGE_NUM}-1) + 1
                AND     #{PAGE_SIZE} * #{PAGE_NUM}

mapper.select:
/* selectPeriodTimeStatistics */ SELECT TO_CHAR(T1.REG_DTTM,'hh24') DT, COUNT(*) AS CNT FROM TB_MOB_EVENT_LOG T1 WHERE T1.REG_DTTM BETWEEN TO_DATE(#{START_DT},'YYYY.MM.DD') AND TO_DATE(#{END_DT},'YYYY.MM.DD hh24:mi:ss')
AND T1.SVC_ID = #{SVC_ID}
GROUP BY TO_CHAR(T1.REG_DTTM,'hh24') ORDER BY TO_CHAR(T1.REG_DTTM,'hh24') ASC

/* selectPeriodDayStatistics */ SELECT TO_CHAR(T1.REG_DTTM,'YYYYMMDD') DT, COUNT(*) AS CNT FROM TB_MOB_EVENT_LOG T1 WHERE T1.REG_DTTM BETWEEN TO_DATE(#{START_DT},'YYYY.MM.DD') AND TO_DATE(#{END_DT},'YYYY.MM.DD hh24:mi:ss')
AND T1.SVC_ID = #{SVC_ID}
GROUP BY TO_CHAR(T1.REG_DTTM,'YYYYMMDD') ORDER BY TO_CHAR(T1.REG_DTTM,'YYYYMMDD') ASC

/* selectPeriodWeekStatistics */ SELECT DAYOFWEEK(TO_CHAR(T1.REG_DTTM, 'YYYY-MM-DD')) DT, COUNT(*) AS CNT FROM TB_MOB_EVENT_LOG T1 WHERE T1.REG_DTTM BETWEEN TO_DATE(#{START_DT},'YYYY.MM.DD') AND TO_DATE(#{END_DT},'YYYY.MM.DD hh24:mi:ss')
AND T1.SVC_ID = #{SVC_ID}
GROUP BY DAYOFWEEK(TO_CHAR(T1.REG_DTTM, 'YYYY-MM-DD')) ORDER BY DAYOFWEEK(TO_CHAR(T1.REG_DTTM, 'YYYY-MM-DD')) ASC

/* selectPerioOSStatistics */ SELECT OS_NM, COUNT(*) AS CNT FROM TB_MOB_EVENT_LOG T1 WHERE T1.REG_DTTM BETWEEN TO_DATE(#{START_DT},'YYYY.MM.DD') AND TO_DATE(#{END_DT},'YYYY.MM.DD hh24:mi:ss') AND OS_NM IS NOT NULL
AND T1.SVC_ID = #{SVC_ID}
GROUP BY OS_NM ORDER BY CNT desc

