SELECT APPID ,APPNAME ,ISDELETED ,TO_CHAR(REGDATE, 'YYYY.MM.DD HH24:MI:SS') AS REGDATE ,TO_CHAR(MODDATE, 'YYYY.MM.DD HH24:MI:SS') AS MODDATE ,SVC_ID ,SVC_DTL FROM T_PUSH_APPLICATION WHERE APPID IN
#{PushServiceDto.APPID}
AND ISDELETED = 'N'

