SELECT  A.* 
,       (SELECT COUNT(1) FROM LST) TOT_CNT
FROM    LST A
WHERE   RNUM    BETWEEN #{PAGE_SIZE} * (#{PAGE_NUM}-1) + 1
                AND     #{PAGE_SIZE} * #{PAGE_NUM}

mapper.select:
		SELECT 
			COUNT(*)
		FROM (
		    SELECT
		       A.CUID
		       ,CASE WHEN A.RESERVEDATE = A.REGDATE
			   		THEN 'I'
			    	ELSE 'R'
			    END AS RESERVE_TYPE
		    FROM T_PUSH_MESSAGE A, T_PUSH_MESSAGE_GROUP G
		    WHERE A.RESERVEDATE BETWEEN TO_DATE(#{RESERVEDATE_S}, 'YYYYMMDDHH24MI') AND TO_DATE(#{RESERVEDATE_E}, 'YYYYMMDDHH24MI')
		          AND A.APPID = #{APPID}
		          AND A.MSGGRPSEQNO = G.MSGGRPSEQNO
                  AND G.TYPE = 'M' 
                  AND A.TYPE = 'N'
		          
		          	AND A.SENDERSEQ = #{SENDERSEQ}
		          
		) C 
		INNER JOIN T_PUSH_USER D ON (C.CUID = D.CUID)
			 	
			 	
			 		AND D.CNAME = #{CNAME}
			 	
			 	
			 		AND D.CUID = #{CUID}
			 	
			 	
			 		AND D.PHONENO = #{PHONENO}
			 	
			 	
			 		AND C.RESERVE_TYPE = #{RESERVE_TYPE}

SELECT SEQNO ,RESERVEDATE ,RESERVE_TYPE ,APPID ,CUID ,SENDERSEQ ,SENDERCODE ,MESSAGE ,SEND_YN ,READ_YN ,REGDATE ,#{TOT_CNT}||'' AS TOT_CNT FROM ( SELECT C.SEQNO ,C.RESERVEDATE ,C.RESERVE_TYPE ,C.APPID ,C.CUID ,C.SENDERSEQ ,C.SENDERCODE ,C.MESSAGE ,C.SEND_YN ,C.READ_YN ,C.REGDATE ,ROW_NUMBER() OVER() AS RNUM FROM ( SELECT A.SEQNO ,TO_CHAR(A.RESERVEDATE, 'YYYY.MM.DD HH24:MI:SS') AS RESERVEDATE ,CASE WHEN A.RESERVEDATE = A.REGDATE THEN 'I' ELSE 'R' END AS RESERVE_TYPE ,A.APPID ,A.CUID ,A.SENDERSEQ ,(SELECT SENDERCODE FROM T_PUSH_SENDER_INFO C WHERE A.SENDERSEQ = C.SENDERSEQ) AS SENDERCODE ,A.MESSAGE ,CASE WHEN (SELECT COUNT(*) FROM T_PUSH_COMPLETE B WHERE A.SEQNO = B.SEQNO AND A.CUID = B.CUID AND B.RECEIVEACK = 'S') > 0 THEN 'Y' ELSE 'N' END AS SEND_YN ,CASE WHEN (SELECT COUNT(*) FROM T_PUSH_COMPLETE B WHERE A.SEQNO = B.SEQNO AND A.CUID = B.CUID AND B.RECEIVEACK = 'R') > 0 THEN 'Y' ELSE 'N' END AS READ_YN ,TO_CHAR(A.REGDATE, 'YYYY.MM.DD HH24:MI:SS') AS REGDATE FROM T_PUSH_MESSAGE A, T_PUSH_MESSAGE_GROUP G WHERE A.RESERVEDATE BETWEEN TO_DATE(#{RESERVEDATE_S}, 'YYYYMMDDHH24MI') AND TO_DATE(#{RESERVEDATE_E}, 'YYYYMMDDHH24MI') AND A.APPID = #{APPID} AND A.MSGGRPSEQNO = G.MSGGRPSEQNO AND G.TYPE = 'M' AND A.TYPE = 'N'
AND A.SENDERSEQ = #{SENDERSEQ}
ORDER BY A.SEQNO DESC ) C INNER JOIN T_PUSH_USER D ON (C.CUID = D.CUID)
AND D.CNAME = #{CNAME}
AND D.CUID = #{CUID}
AND D.PHONENO = #{PHONENO}
AND C.RESERVE_TYPE = #{RESERVE_TYPE}
) WHERE RNUM BETWEEN #{START_NUM} AND #{END_NUM}
