	, #{FILE_PATH,jdbcType=VARCHAR}, #{FILE_SIZE,jdbcType=VARCHAR}, #{FILE_TYPE,jdbcType=CHAR}, #{THUMB_PATH,jdbcType=VARCHAR}, (CURRENT TIMESTAMP))

