)
SELECT  A.* 
,       (SELECT COUNT(1) FROM LST) TOT_CNT
FROM    LST A
WHERE   RNUM    BETWEEN #{PAGE_SIZE} * (#{PAGE_NUM}-1) + 1
                AND     #{PAGE_SIZE} * #{PAGE_NUM}

mapper.select:
SELECT T1.GRP_ID ,T1.EMP_NO ,T1.DEPT_CD ,T2.DEPT_NM ,T2.FULL_NM ,T1.USER_ID ,T3.USER_NM ,T4.FULL_NM AS USER_FULL_NM FROM TB_SPUSH_GROUP_USER T1 LEFT OUTER JOIN TB_SPUSH_DEPT T2 ON T1.DEPT_CD = T2.DEPT_CD LEFT OUTER JOIN TB_SPUSH_USER T3 ON T1.USER_ID = T3.USER_ID AND T3.USE_YN = 'Y' LEFT OUTER JOIN TB_SPUSH_DEPT T4 ON T3.DEPT_CD = T4.DEPT_CD WHERE
T1.GRP_ID = #{GRP_ID}
ORDER BY T1.GRP_ID

mapper.insert:
    	INSERT INTO TB_SPUSH_GROUP(
    		GRP_ID
    		, GRP_NM
    		, GRP_COLOR
    		, REG_ID
    		, REG_DT
    	)VALUES(
    		SEQ_TB_SPUSH_GROUP.NEXTVAL
    		, #{GRP_NM,jdbcType=VARCHAR}
    		, #{GRP_COLOR,jdbcType=VARCHAR}
    		, #{REG_ID,jdbcType=VARCHAR}
    		, (CURRENT TIMESTAMP)
    	)
    	
		 	SELECT SEQ_TB_SPUSH_GROUP.currval as GRP_ID FROM SYSIBM.SYSDUMMY1

        INSERT INTO TB_SPUSH_GROUP_USER(GRP_ID, EMP_NO, DEPT_CD, USER_ID)  
        
			SELECT #{item.GRP_ID,jdbcType=INTEGER}, #{item.EMP_NO,jdbcType=VARCHAR}, #{item.DEPT_CD,jdbcType=VARCHAR}
				, #{item.USER_ID,jdbcType=VARCHAR} FROM SYSIBM.SYSDUMMY1

mapper.delete:
DELETE FROM TB_SPUSH_GROUP 
WHERE 
	REG_ID=#{REG_ID} AND GRP_ID=#{GRP_ID}
