mapper.select:
SELECT T2.MSGSEQ ,T2.TITLE ,T2.CONT_TYPE ,T2.SENDER_ID ,T3.USER_NM AS SENDER_NM ,T3.DEPT_CD ,T3.DEPT_NM ,T2.VOLATILE_YN ,T2.FORWARD_YN ,T2.REF_ID ,TO_CHAR(T2.REF_DT, 'YYYYMMDDHH24MISS') AS REF_DT ,T2.REF_SENDER ,T4.USER_NM AS REF_SENDER_NM ,T4.DEPT_CD AS REF_SENDER_DEPT_CD ,T4.DEPT_NM AS REF_SENDER_DEPT_NM ,TO_CHAR(T2.EXPIRE_DT, 'YYYYMMDDHH24MISS') AS EXPIRE_DT ,TO_CHAR(T2.REG_DT, 'YYYYMMDDHH24MISS') AS REG_DT ,T2.SEND_STATUS ,T2.SENT_RESULT_CD ,T2.URGENT_YN ,T2.RECV_TYPE ,T2.RECV_NM ,T2.ATTACH_YN ,T2.RESERV_SEQ ,T1.USER_ID ,T1.READYN ,T1.RECV_GRPID ,T5.GRP_NM ,T5.GRP_COLOR ,T1.STORE_YN FROM ( SELECT MSGSEQ, USER_ID, READYN, RECV_GRPID, STORE_YN, REG_DT FROM TB_SPUSH_MSG_USER
STATUS = '0'
AND USER_ID = #{USER_ID}
AND STORE_YN = #{STORE_YN}
AND READYN = #{READ_YN}
AND RECV_GRPID = #{GRP_ID}
) T1 INNER JOIN TB_SPUSH_MSG T2 ON T1.MSGSEQ = T2.MSGSEQ LEFT OUTER JOIN TB_SPUSH_USER T3 ON T2.SENDER_ID = T3.USER_ID LEFT OUTER JOIN TB_SPUSH_USER T4 ON T2.REF_SENDER = T4.USER_ID LEFT OUTER JOIN TB_SPUSH_GROUP T5 ON T1.RECV_GRPID = T5.GRP_ID
AND T2.VOLATILE_YN = #{VOLATILE_YN}
AND T2.URGENT_YN = #{URGENT_YN}
AND T2.TITLE LIKE '%' || #{TITLE} || '%'
ORDER BY T2.MSGSEQ DESC

SELECT FILE_ID ,MSGSEQ ,RESERV_SEQ ,FILE_NM ,FILE_EXT ,FILE_PATH ,FILE_SIZE ,FILE_TYPE ,THUMB_PATH ,TO_CHAR(REG_DT, 'YYYY.MM.DD HH24:MI:SS') AS REG_DT FROM TB_SPUSH_MSG_FILE
MSGSEQ = #{MSGSEQ}
ORDER BY FILE_ID

SELECT T1.MSGSEQ ,COUNT(T1.MSGSEQ) AS TOTAL_CNT , NVL(SUM(T1.READ_FLAG),0) AS READ_CNT FROM( SELECT MSGSEQ, CASE READYN WHEN 'Y' THEN 1 ELSE 0 END AS READ_FLAG FROM TB_SPUSH_MSG_USER WHERE MSGSEQ IN
#{item}
) T1 GROUP BY T1.MSGSEQ

