SELECT  A.* 
,       (SELECT COUNT(1) FROM LST) TOT_CNT
FROM    LST A
WHERE   RNUM    BETWEEN #{PAGE_SIZE} * (#{PAGE_NUM}-1) + 1
                AND     #{PAGE_SIZE} * #{PAGE_NUM}

mapper.insert:

INSERT INTO TB_STO_USERDEVC(
	   USERDEVC_IDX, EMP_NO, DEVC_IDX, GUBUN_CD, REG_ID, REG_DTTM, PHONE_NO
) VALUES (
	   SEQ_TB_STO_USERDEVC.NEXTVAL
	 , #{EMP_NO}
	 , #{DEVC_IDX}
	 , #{GUBUN_CD}
	 , #{REG_ID}
	 , (CURRENT TIMESTAMP)
	 , #{PHONE_NO}
)

mapper.update:

UPDATE TB_STO_USERDEVC SET
	   GUBUN_CD  = '2'  
WHERE  EMP_NO    = #{EMP_NO}


UPDATE TB_STO_USERDEVC SET
       DEVC_IDX     = #{DEVC_IDX}
	 , GUBUN_CD     = #{GUBUN_CD}
	 , PHONE_NO     = #{PHONE_NO}
WHERE  USERDEVC_IDX = #{USERDEVC_IDX}

mapper.select:
SELECT USR.EMP_NO, USR.EMP_NM, GRP.GRP_NM FROM TB_STO_USER USR LEFT JOIN TB_STO_USERGROUP UG ON USR.EMP_NO = UG.EMP_NO AND UG.USE_YN = 'Y' LEFT JOIN TB_STO_GROUP GRP ON UG.GRP_ID = GRP.GRP_ID AND GRP.USE_YN = 'Y' WHERE USR.USE_YN = 'Y'
AND USR.EMP_NO = #{EMP_NO}
AND USR.EMP_NM LIKE '%'||#{EMP_NM}||'%'
