mapper.select:
SELECT APPID ,APPNAME ,ISDELETED ,CAST(REGDATE AS DATE) AS REGDATE ,CAST(MODDATE AS DATE) AS MODDATE ,SVC_ID ,SVC_DTL FROM T_PUSH_APPLICATION WHERE APPID IN
#{PushServiceDto.APPID}
AND ISDELETED = 'N'

mapper.update:
UPDATE T_PUSH_APPLICATION
    SET APPNAME  = #{APPNAME}
       ,SVC_DTL = #{SVC_DTL}
WHERE APPID = #{APPID}

    	UPDATE T_PUSH_PNS_INFO
    		SET KEYSTORE = #{KEYSTORE}
				,PASSWORD = #{PASSWORD}
			
				,CERT = #{CERT}
			
			WHERE 	APPID = #{APPID}
    		AND 	PNSID = #{PNSID}
