WHERE RNUM BETWEEN ((#{PAGE_NUM} - 1) *  #{PAGE_SIZE}) + 1 AND (#{PAGE_NUM} *  #{PAGE_SIZE})

mapper.select:
SELECT BOARD_IDX , SVC_ID , BOARD_TYPE , BOARD_DETAIL_TYPE, BOARD_TITLE , BOARD_CONTENT , HIT , USE_YN , REG_ID , TO_CHAR(REG_DT, '%Y.%m.%d') REG_DT , TO_CHAR(MOD_DT, '%Y.%m.%d') MOD_DT , MOD_ID , SECRET_YN , ( SELECT CD_NM FROM TB_SYS_CD_INFO WHERE CD_GRP_ID = (CASE BOARD_TYPE WHEN 'NOTICE' THEN 'ST001' ELSE 'ST005' END) AND CD_ID=BOARD_DETAIL_TYPE ) BOARD_DETAIL_NAME , REF , STP , LVL FROM TB_STO_BOARD A WHERE BOARD_TYPE = #{BOARD_TYPE}
AND A.SECRET_YN = #{SECRET_YN}
AND A.BOARD_TITLE LIKE '%' || #{BOARD_TITLE} || '%'
ORDER BY ${ORDER_TARGET} ${ORDER_TYPE}, STP ASC
ORDER BY BOARD_IDX DESC, STP ASC

mapper.update:
        
		UPDATE TB_STO_BOARD
		   SET HIT       = HIT + 1
		 WHERE BOARD_IDX = #{BOARD_IDX}

mapper.select:
	    
		SELECT A.FILE_IDX, A.FILE_PATH, A.FILE_NAME, A.FILE_SAVE_NAME, A.FILE_EXT, A.FILE_SIZE
        FROM   TB_STO_FILE A
		WHERE  A.BOARD_IDX = #{BOARD_IDX}

mapper.insert:
        
        
			SELECT	NVL(MAX(BOARD_IDX), 0) + 1
			FROM	TB_STO_BOARD
        
        INSERT INTO TB_STO_BOARD (
              BOARD_IDX, SVC_ID, BOARD_TYPE, BOARD_DETAIL_TYPE, BOARD_TITLE, BOARD_CONTENT, HIT, USE_YN, REG_ID, REG_DT, SECRET_YN
            , REF , STP , LVL
        ) VALUES (
              #{BOARD_IDX}
            , (SELECT SVC_ID FROM TB_STO_SVC WHERE USE_YN = 'Y')
            , #{BOARD_TYPE}
            , #{BOARD_DETAIL_TYPE}
            , #{BOARD_TITLE}
            , #{BOARD_CONTENT}
            , 0
            , #{USE_YN}
            , #{REG_ID}
            , CURRENT
            , #{SECRET_YN}
            , CASE WHEN #{REF}  = '' THEN  #{BOARD_IDX} ELSE #{REF} END
            , ${STP}
            , ${LVL}
        )

mapper.update:

UPDATE TB_STO_BOARD SET
       STP =  STP + 1
 WHERE REF =  #{REF}
   AND STP >= #{STP}

        
		UPDATE TB_STO_BOARD
           SET BOARD_DETAIL_TYPE = #{BOARD_DETAIL_TYPE}
             , BOARD_TITLE       = #{BOARD_TITLE}
             , BOARD_CONTENT     = #{BOARD_CONTENT}
             , USE_YN            = #{USE_YN}
             , MOD_DT            = CURRENT
             , MOD_ID            = #{REG_ID}
             , SECRET_YN         = #{SECRET_YN}
         WHERE BOARD_IDX         = #{BOARD_IDX}

mapper.insert:
    
	INSERT INTO TB_STO_FILE (
		   BOARD_IDX, FILE_NAME, FILE_SAVE_NAME, FILE_PATH, FILE_EXT, FILE_SIZE, STS_CD, REG_ID, REG_DT
	) VALUES (
		   #{BOARD_IDX}
		 , #{FILE_NAME}
		 , #{FILE_SAVE_NAME}
		 , #{FILE_PATH}
		 , #{FILE_EXT}
		 , #{FILE_SIZE}
		 , #{STS_CD}
		 , #{REG_ID}
		 , CURRENT
	)

mapper.select:

SELECT COUNT(*) FROM TB_STO_BOARD 
WHERE  REF = (
            SELECT REF  FROM TB_STO_BOARD 
            WHERE  BOARD_IDX = #{BOARD_IDX}
)
AND    LVL >  (
            SELECT LVL  FROM TB_STO_BOARD 
            WHERE  BOARD_IDX = #{BOARD_IDX}
)
