mapper.select:
/* UserGroupManageSql SelectUserGroupList*/
SELECT * FROM ( SELECT A.UG_IDX, A.GRP_ID, B.GRP_NM, A.EMP_NO, C.EMP_NM , C.USERID, TO_CHAR(A.REG_DT, '%Y.%m.%d') REG_DT, C.EMP_CD, (SELECT CD_NM FROM TB_STO_EMP_CODE WHERE EMP_CD = C.EMP_CD) CD_NM FROM TB_STO_USERGROUP A, TB_STO_GROUP B, TB_STO_USER C WHERE A.GRP_ID = B.GRP_ID AND A.EMP_NO = C.EMP_NO AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' AND C.USE_YN = 'Y'
AND C.EMP_NM LIKE '%' || #{SEARCH_EMP_NM} || '%'
AND B.GRP_NM LIKE '%' || #{SEARCH_GRP_NM} || '%'
)
ORDER BY ${ORDER_TARGET} ${ORDER_TYPE}

/*  UserGroupManageSql saveUserGroup  */
SELECT 	COUNT(1) 
FROM		TB_STO_USERGROUP
WHERE 	GRP_ID   = #{GRP_ID}
    AND 		EMP_NO = #{EMP_NO}
    AND 		USE_YN = 'Y'

mapper.update:
/* UserGroupManageSql  deleteUserGroup  */
 UPDATE TB_STO_USERGROUP
 SET USE_YN = 'N'
  , MOD_ID      = #{MOD_ID} 
  , MOD_DT = CURRENT
 WHERE  UG_IDX = #{UG_IDX}
