SELECT TOP (SELECT PAGE_SIZE FROM PAGE_INFO) *
, (SELECT COUNT(1) FROM LST) TOT_CNT
FROM LST 
WHERE	PAGE_KEY NOT IN
		(
			SELECT TOP (((SELECT PAGE_NUM FROM PAGE_INFO)  - 1) * (SELECT PAGE_SIZE FROM PAGE_INFO) ) PAGE_KEY
