mapper.select:
SELECT A.APP_ID, A.APP_VER, A.OS_TYPE, A.FRA_DESC, A.MOD_DTTM, B.SVC_NM , ROW_NUMBER() OVER(ORDER BY A.MOD_DTTM DESC) PAGE_KEY FROM TB_FRA_SVC AS A LEFT OUTER JOIN TB_SVC AS B ON A.APP_ID = B.APP_ID
AND A.APP_ID = #{APP_ID}
AND A.OS_TYPE = #{OS_TYPE}

mapper.update:
		UPDATE TB_FRA_SVC
    	SET HASH_BUILD_VER = #{HASH_BUILD_VER}
	       ,FRA_DESC = #{FRA_DESC}
	       ,MOD_ID = #{MOD_ID}
	       ,MOD_DTTM = getdate()
		WHERE APP_ID = #{APP_ID} AND APP_VER = #{APP_VER} AND OS_TYPE = #{OS_TYPE}

mapper.select:
SELECT FILE_NM
FROM TB_FRA_SVC 
WHERE APP_ID = #{APP_ID} AND APP_VER = #{APP_VER} AND OS_TYPE = #{OS_TYPE}
