SELECT TOP (SELECT PAGE_SIZE FROM PAGE_INFO) *
, (SELECT COUNT(1) FROM LST) TOT_CNT
FROM LST 
WHERE	PAGE_KEY NOT IN
		(
			SELECT TOP (((SELECT PAGE_NUM FROM PAGE_INFO)  - 1) * (SELECT PAGE_SIZE FROM PAGE_INFO) ) PAGE_KEY
			FROM LST 
		)

mapper.select:
SELECT A.MSGGRPSEQNO ,A.SEQNO ,REPLACE(CONVERT(VARCHAR(16), A.RESERVEDATE, 120), '-' , '.') AS RESERVEDATE ,A.APPID ,A.GROUPSEQ ,A.SENDERSEQ ,(SELECT SENDERCODE FROM T_PUSH_SENDER_INFO B WHERE A.SENDERSEQ = B.SENDERSEQ) AS SENDERCODE ,A.MESSAGE ,REPLACE(CONVERT(VARCHAR(16), A.REGDATE, 120), '-' , '.') AS REGDATE ,A.STATE , ROW_NUMBER() OVER(ORDER BY A.RESERVEDATE ASC, MSGGRPSEQNO) PAGE_KEY FROM T_PUSH_MESSAGE_GROUP A WHERE A.RESERVEDATE != A.REGDATE AND A.RESERVEDATE > GETDATE() AND A.RESERVEDATE BETWEEN (SELECT CONVERT(DATETIME, SUBSTRING(DT, 1, 8) + ' ' + SUBSTRING(DT, 9, 2) + ':' + SUBSTRING(DT, 11, 2)) FROM( SELECT #{RESERVEDATE_S} DT ) A) AND (SELECT CONVERT(DATETIME, SUBSTRING(DT, 1, 8) + ' ' + SUBSTRING(DT, 9, 2) + ':' + SUBSTRING(DT, 11, 2)) FROM( SELECT #{RESERVEDATE_E} DT ) A) AND A.APPID = #{APPID}
AND A.SENDERSEQ = #{SENDERSEQ}
AND A.GROUPSEQ = #{GROUPSEQ}
