mapper.update:
    	UPDATE TB_SVC
		    SET SVC_NM  = #{SVC_NM}
		       ,SVC_DTL  = #{SVC_DTL}
		WHERE SVC_ID = #{SVC_ID}

mapper.insert:
    	INSERT INTO TB_SVC(
			SVC_NM,
  			REG_DTTM,
  			APP_ID,
  			SVC_DTL
		)VALUES(
		  	#{SVC_NM}
			,NOW()
			,#{APP_ID}
			,#{SVC_DTL}
		)
		
		 	SELECT LAST_INSERT_ID() as SVC_ID

mapper.update:
    	UPDATE TB_MOB_SVC SET
		    USE_YN = #{USE_YN}
		WHERE SVC_ID = #{SVC_ID}

    	UPDATE TB_STO_SVC SET
		    USE_YN = #{USE_YN}
		WHERE SVC_ID = #{SVC_ID}

    	UPDATE T_PUSH_APPLICATION SET
		    ISDELETED = #{USE_YN}
		WHERE SVC_ID = #{SVC_ID}
