mapper.delete:
    DELETE FROM 
    	TB_MOB_RSC_TEMP_FILE 
    WHERE 
    	SVC_ID = #{SVC_ID} 
    	AND FILE_EXT IN 
    	
			#{FILE_EXT}

mapper.select:
/* MobileResourceUpdateSql - SelsectResourceTempCount */
SELECT 
	*
FROM 
	TB_MOB_RSC_FILE
WHERE
	SVC_ID = #{SVC_ID}
	AND FILE_PATH = #{FILE_PATH}

mapper.update:
	/* MobileResourceUpdateSql - UpdateResourceTemp */
    UPDATE TB_MOB_RSC_FILE
	
		MOD_DTTM = SYSDATE ,
			FILE_SIZE = #{FILE_SIZE},
			STS_CD = #{STS_CD},
			DEL_YN = 'N'
	
	WHERE 
		SVC_ID = #{SVC_ID}
    	AND FILE_PATH = #{FILE_PATH}
