mapper.update:
UPDATE TB_SYS_MENU
    SET MENU_NM     = #{NM_MENU}
       ,MENU_URL    = #{MENU_URL}
       ,SORT_NO    = #{NO_ORDER}
       ,POPUP_YN    = #{YN_POPUP}
       ,POPUP_W    = #{NO_WIDTH}
       ,POPUP_H   = #{NO_HEIGHT}
       ,STS_CD      = #{YN_USE}
       ,MENU_DESC   = #{DESC_MENU}
       ,MOD_ID   = #{ID_UPDATE}
       ,MOD_DTTM   = SYSDATE
WHERE MENU_ID =  #{ID_MENU}

mapper.insert:
INSERT INTO TB_SYS_MENU(
             MENU_ID
            ,UP_MENU_ID
            ,MENU_NM
            ,MENU_URL
            ,SORT_NO
            ,POPUP_YN
            ,POPUP_W
            ,POPUP_H
            ,STS_CD
            ,MENU_DESC
            ,REG_ID
            ,REG_DTTM
        )VALUES(
             (SELECT LPAD(NVL(MAX(MENU_ID), 0)+1, 10, '0') AS MENU_ID FROM TB_SYS_MENU)
            ,#{ID_MENU_PARENT}
            ,#{NM_MENU}
            ,#{MENU_URL}
            ,#{NO_ORDER}
            ,#{YN_POPUP}
            ,#{NO_WIDTH}
            ,#{NO_HEIGHT}
            ,#{YN_USE}
            ,#{DESC_MENU}
            ,#{ID_INSERT}
            ,SYSDATE
        )

mapper.select:
    	DELETE FROM TB_SYS_FAVRT
        WHERE	MENU_ID IN
        
			#{AdminMenuDTO.MENU_ID}

    	DELETE FROM TB_SYS_MENU_AUTH
        WHERE	MENU_ID IN
        
			#{AdminMenuDTO.MENU_ID}

mapper.delete:
		DELETE FROM TB_SYS_MENU
		WHERE	MENU_ID IN
        
			#{AdminMenuDTO.MENU_ID}
