mapper.select:
SELECT RSC_FILE_IDX , FILE_NM , SUBSTR( FILE_PATH , LENGTH ( #{DEPLOY_PATH} ) + 1 ) AS FILE_PATH , FILE_EXT , FILE_TP , FILE_SIZE , DEL_YN , STS_CD , TO_CHAR ( REG_DTTM , 'YYYY.MM.DD HH24:MI:SS') AS REG_DTTM , TO_CHAR ( MOD_DTTM , 'YYYY.MM.DD HH24:MI:SS') AS MOD_DTTM , SVC_ID , RSC_ID FROM TB_MOB_RSC_FILE WHERE SVC_ID = #{SVC_ID}
AND FILE_NM LIKE '%' || #{FILE_NM} || '%'
AND FILE_EXT = #{FILE_EXT}
AND STS_CD = #{STS_CD}
AND MOD_DTTM BETWEEN TO_DATE(#{RESOURCEDTTM_S}, 'YYYYMMDDHH24MI') AND TO_DATE(#{RESOURCEDTTM_E}, 'YYYYMMDDHH24MI')
ORDER BY FILE_NM DESC
ORDER BY FILE_NM ASC
ORDER BY MOD_DTTM DESC
ORDER BY MOD_DTTM ASC

    SELECT 	NVL(MAX( TO_NUMBER( DPLY_VER) ),0)  + 1 VERSION 
	FROM 		TB_MOB_DPLY
	WHERE SVC_ID = #{SVC_ID}

SELECT * FROM TB_MOB_RSC_FILE
where RSC_FILE_IDX in

	#{DEPLOY_ADD_LIST}

mapper.insert:
INSERT INTO TB_MOB_DPLY
	( DPLY_IDX
	, DPLY_NM
	, DPLY_VER
	, DPLY_DESC
	, DPLY_TP
	, USE_YN
	, SVC_ID
	, DPLY_FULL_YN
	, REF_VER
	, REG_DTTM
	,REG_ID )
VALUES
	( SEQ_TB_MOB_DPLY.NEXTVAL
	, #{DPLY_NM}
	, #{DPLY_VER}
	, #{DPLY_DESC}
	, #{DPLY_TP}
	, #{USE_YN}
	, #{SVC_ID}
	, #{DPLY_FULL_YN}
	, #{REF_VER}
	, SYSDATE 
	, #{REG_ID} )

INSERT INTO TB_FILE_ATTACH
	( FILE_IDX
	, FILE_ID
	, FILE_PATH
	, FILE_NAME
	, FILE_EXT
	, FILE_SIZE
	, FILE_TYPE
	, URL_CHILD_PATH
	, OBJ_CODE
	, OBJ_ID
	, REG_DATE
	, MD5 )
VALUES
	( SEQ_TB_FILE_ATTACH.NEXTVAL
	, #{FILE_ID}
	, #{FILE_PATH}
	, #{FILE_NAME}
	, #{FILE_EXT}
	, #{FILE_SIZE}
	, #{FILE_TYPE}
	, #{URL_CHILD_PATH}
	, #{OBJ_CODE}
	, #{OBJ_ID}
	, SYSDATE
	, #{MD5} )

INSERT INTO TB_MOB_DPLY_DTL
	( DPLY_DTL_IDX
	, DPLY_IDX
	, FILE_NM
	, FILE_PATH
	, FILE_SIZE
	, DEL_YN
	, STS_CD
	, REG_DTTM
	, SVC_ID
	, RSC_ID )
SELECT 
	SEQ_TB_MOB_DPLY_DTL.NEXTVAL
	, #{DPLY_IDX}
	, #{FILE_NM}
	, #{FILE_PATH}
	, #{FILE_SIZE}
	, #{DEL_YN}
	, #{STS_CD}
	, SYSDATE
	, #{SVC_ID} 
	, #{RSC_ID}
FROM DUAL

