mapper.select:
/* CategoryManageSql selectCategoryTreeList*/ SELECT A.*, ROWNUM ROW_NUM, LEVEL AS MENU_LEVEL,
CASE WHEN MAX(ROWNUM) OVER (PARTITION BY ROWNUM - LEVEL) = ROWNUM THEN 1 ELSE 0 END LAST_CHK FROM( SELECT A.CATG_CD, A.CATG_NM, A.UP_CATG_CD, A.CATG_DESC, A.USE_YN, A.SORT_NO, A.REG_ID, TO_CHAR (A.REG_DT, 'YYYY.MM.DD') AS REG_DT FROM TB_STO_CATG A, TB_STO_SVC B WHERE A.SVC_ID = B.SVC_ID AND B.USE_YN = 'Y' AND A.USE_YN = 'Y' ) A
START WITH A.CATG_CD = #{CATG_CD}
START WITH A.UP_CATG_CD = #{CATG_CD}
CONNECT BY PRIOR A.CATG_CD = A.UP_CATG_CD ORDER SIBLINGS BY A.SORT_NO, A.CATG_NM

/* CategoryManageSql selectCategoryDetailList*/ SELECT A.*, ROWNUM ROW_NUM, LEVEL AS MENU_LEVEL,
CASE WHEN MAX(ROWNUM) OVER (PARTITION BY ROWNUM - LEVEL) = ROWNUM THEN 1 ELSE 0 END LAST_CHK FROM( SELECT A.CATG_CD, A.CATG_NM, A.UP_CATG_CD, A.CATG_DESC, A.USE_YN, A.SORT_NO, A.REG_ID, TO_CHAR (A.REG_DT, 'YYYY.MM.DD') AS REG_DT FROM TB_STO_CATG A, TB_STO_SVC B WHERE A.SVC_ID = B.SVC_ID AND B.USE_YN = 'Y' ) A
START WITH A.CATG_CD = #{CATG_CD}
START WITH A.UP_CATG_CD = #{CATG_CD}
CONNECT BY PRIOR A.CATG_CD = A.UP_CATG_CD ORDER SIBLINGS BY A.SORT_NO, A.CATG_NM

/*  CategoryManageSql   selectCategoryCount*/
SELECT COUNT(1)
FROM TB_STO_CATG
WHERE CATG_CD = #{CATG_CD}

mapper.update:
/*  CategoryManageSql   insertCategory*/
INSERT INTO TB_STO_CATG
(
       CATG_CD, CATG_NM, SORT_NO, USE_YN, UP_CATG_CD, CATG_DESC, SVC_ID, REG_ID, REG_DT
) VALUES (
       SEQ_TB_STO_CATG.NEXTVAL
     , #{CATG_NM}
     , #{SORT_NO}
     , #{USE_YN}
     , #{UP_CATG_CD}
     , #{CATG_DESC}
     , #{SVC_ID}
     , #{REG_ID}
     , SYSDATE
)

/*  CategoryManageSql   SaveCategory*/
UPDATE TB_STO_CATG
SET
       CATG_NM = #{CATG_NM}
     , SORT_NO = #{SORT_NO}
     , USE_YN   = #{USE_YN}
     , CATG_DESC   = #{CATG_DESC}
     , MOD_ID   = #{MOD_ID}
     , MOD_DT  = SYSDATE
WHERE CATG_CD = #{CATG_CD}

/*  CategoryManageSql   updateAllCategoryUseYn*/
UPDATE TB_STO_CATG
SET
       USE_YN   = #{USE_YN}
     , MOD_ID   = #{MOD_ID}
     , MOD_DT  = SYSDATE
WHERE UP_CATG_CD = #{CATG_CD}

mapper.select:
SELECT COUNT(*)
FROM TB_STO_APP

WHERE CATG_CD = #{value}

mapper.delete:
/*  CategoryManageSql   deleteCategory*/
DELETE FROM TB_STO_CATG
WHERE 
	CATG_CD = #{CATG_CD}
