mapper.select:
WITH "SA" AS ( SELECT "SA"."MENU_ID" , "SAG"."INTRO_MENU_ID" , "SA"."AUTH_GRP_ID" , "SA"."SELECT_YN" , "SA"."INSERT_YN" , "SA"."UPDATE_YN" , "SA"."DELETE_YN" , "SA"."EXCEL_YN" FROM "TB_SYS_MENU_AUTH" "SA" INNER JOIN "TB_SYS_USER_AUTH_GRP" "SUAG" ON "SA"."AUTH_GRP_ID" = "SUAG"."AUTH_GRP_ID" INNER JOIN "TB_SYS_AUTH_GRP" "SAG" ON "SUAG"."AUTH_GRP_ID" = "SAG"."AUTH_GRP_ID" WHERE ("SA"."SELECT_YN" != 'N' OR "SA"."INSERT_YN" != 'N' OR "SA"."UPDATE_YN" != 'N' OR "SA"."DELETE_YN" != 'N' OR "SA"."EXCEL_YN" != 'N')
AND "SAG"."AUTH_GRP_ID" = #{AUTH_GRP_ID}
AND "SUAG"."USER_ID" = #{USER_ID}
) SELECT "MN"."MENU_ID", "MN"."UP_MENU_ID", "MN"."MENU_NM", "MN"."MENU_URL", "MN"."STS_CD" , "SA"."INTRO_MENU_ID" AS "INTRO_MENU_ID" , "SA"."AUTH_GRP_ID" AS "AUTH_GRP_ID" , "SA"."SELECT_YN" AS "SELECT_YN" , "SA"."INSERT_YN" AS "INSERT_YN" , "SA"."UPDATE_YN" AS "UPDATE_YN" , "SA"."DELETE_YN" AS "DELETE_YN" , "SA"."EXCEL_YN" AS "EXCEL_YN" , "MN"."SORT_NO" , "MN"."REG_ID", "MN"."REG_DTTM", "MN"."MOD_ID", "MN"."MOD_DTTM" FROM "TB_SYS_MENU" "MN" INNER JOIN "SA" ON "MN"."MENU_ID" = "SA"."MENU_ID"
"MN"."STS_CD" = 'Y'
ORDER BY "MN"."SORT_NO"

WITH "SA" AS ( SELECT "SA"."MENU_ID" , "SAG"."INTRO_MENU_ID" , "SA"."AUTH_GRP_ID" AS "AUTH_GRP_ID" , "SA"."SELECT_YN" AS "SELECT_YN" , "SA"."INSERT_YN" AS "INSERT_YN" , "SA"."UPDATE_YN" AS "UPDATE_YN" , "SA"."DELETE_YN" AS "DELETE_YN" , "SA"."EXCEL_YN" AS "EXCEL_YN" FROM "TB_SYS_MENU_AUTH" "SA" INNER JOIN "TB_SYS_AUTH_GRP" "SAG" ON "SA"."AUTH_GRP_ID" = "SAG"."AUTH_GRP_ID" WHERE ("SA"."SELECT_YN" != 'N' OR "SA"."INSERT_YN" != 'N' OR "SA"."UPDATE_YN" != 'N' OR "SA"."DELETE_YN" != 'N' OR "SA"."EXCEL_YN" != 'N')
AND "SAG"."AUTH_GRP_ID" = #{AUTH_GRP_ID}
AND "SAG"."AUTH_GRP_ID" = (SELECT "AUTH_GRP_ID" FROM "TB_SYS_USER_AUTH_GRP" WHERE "USER_ID" = #{USER_ID})
) SELECT "MN"."MENU_ID", "MN"."UP_MENU_ID", "MN"."MENU_NM", "MN"."MENU_URL", "MN"."STS_CD" , "SA"."INTRO_MENU_ID" AS "INTRO_MENU_ID" , "SA"."AUTH_GRP_ID" AS "AUTH_GRP_ID" , "SA"."SELECT_YN" AS "SELECT_YN" , "SA"."INSERT_YN" AS "INSERT_YN" , "SA"."UPDATE_YN" AS "UPDATE_YN" , "SA"."DELETE_YN" AS "DELETE_YN" , "SA"."EXCEL_YN" AS "EXCEL_YN" , "MN"."SORT_NO" , "MN"."REG_ID", "MN"."REG_DTTM", "MN"."MOD_ID", "MN"."MOD_DTTM" FROM "TB_SYS_MENU" "MN" LEFT JOIN "SA" ON "MN"."MENU_ID" = "SA"."MENU_ID"
"MN"."STS_CD" = 'Y'
ORDER BY "MN"."SORT_NO"
