mapper.select:
SELECT TO_CHAR(T1."REGDATE",'hh24') AS "DT", COUNT(*) AS "CNT" FROM t_push_user_info AS T1 WHERE T1."REGDATE" BETWEEN TO_DATE(#{START_DT}, 'YYYY.MM.DD') AND TO_DATE(#{END_DT}, 'YYYY.MM.DD hh24:mi:ss')
AND T1."APPID" = #{APPID, jdbcType=VARCHAR}
AND T1."PNSID" = #{PNSID}
GROUP BY TO_CHAR(T1."REGDATE", 'hh24') ORDER BY TO_CHAR(T1."REGDATE", 'hh24') ASC

SELECT TO_CHAR(T1."REGDATE", 'YYYYMMDD') AS "DT", COUNT(*) AS "CNT" FROM t_push_user_info AS T1 WHERE T1."REGDATE" BETWEEN TO_DATE(#{START_DT}, 'YYYY.MM.DD') AND TO_DATE(#{END_DT}, 'YYYY.MM.DD hh24:mi:ss')
AND T1."APPID" = #{APPID, jdbcType=VARCHAR}
AND T1."PNSID" = #{PNSID}
GROUP BY TO_CHAR(T1."REGDATE", 'YYYYMMDD') ORDER BY TO_CHAR(T1."REGDATE", 'YYYYMMDD') ASC

SELECT TO_CHAR(T1."REGDATE", 'd') AS "DT", COUNT(*) AS "CNT" FROM t_push_user_info AS T1 WHERE T1."REGDATE" BETWEEN TO_DATE(#{START_DT}, 'YYYY.MM.DD') AND TO_DATE(#{END_DT}, 'YYYY.MM.DD hh24:mi:ss')
AND T1."APPID" = #{APPID, jdbcType=VARCHAR}
AND T1."PNSID" = #{PNSID}
GROUP BY TO_CHAR(T1."REGDATE", 'd') ORDER BY TO_CHAR(T1."REGDATE", 'd') ASC

SELECT TO_CHAR(T1."REGDATE", 'YYYYMM') AS "DT", COUNT(*) AS "CNT" FROM t_push_user_info AS T1 WHERE T1."REGDATE" BETWEEN TO_DATE(#{START_DT}, 'YYYY.MM.DD') AND TO_DATE(#{END_DT}, 'YYYY.MM.DD hh24:mi:ss')
AND T1."APPID" = #{APPID, jdbcType=VARCHAR}
AND T1."PNSID" = #{PNSID}
GROUP BY TO_CHAR(T1."REGDATE", 'YYYYMM') ORDER BY TO_CHAR(T1."REGDATE", 'YYYYMM') ASC

SELECT "PNSID", COUNT(*) AS "CNT" FROM t_push_user_info AS T1 WHERE T1."REGDATE" BETWEEN TO_DATE(#{START_DT}, 'YYYY.MM.DD') AND TO_DATE(#{END_DT}, 'YYYY.MM.DD hh24:mi:ss')
AND T1."APPID" = #{APPID, jdbcType=VARCHAR}
AND T1."PNSID" = #{PNSID}
GROUP BY "PNSID" ORDER BY "CNT" desc

SELECT "SENDDATE" ,"SENDERCODE" ,"SENT_CNT" ,"FAIL_CNT" ,"READ_CNT" ,"UPNS_SENT_CNT" ,"UPNS_FAIL_CNT" ,"APNS_SENT_CNT" ,"APNS_FAIL_CNT" ,"GCM_SENT_CNT" ,"GCM_FAIL_CNT" ,"TIME00" ,"TIME01" ,"TIME02" ,"TIME03" ,"TIME04" ,"TIME05" ,"TIME06" ,"TIME07" ,"TIME08" ,"TIME09" ,"TIME10" ,"TIME11" ,"TIME12" ,"TIME13" ,"TIME14" ,"TIME15" ,"TIME16" ,"TIME17" ,"TIME18" ,"TIME19" ,"TIME20" ,"TIME21" ,"TIME22" ,"TIME23" FROM t_push_sysmsg_sentinfo
"SENDDATE" >= #{START_DT} AND "SENDDATE"<=#{END_DT}
AND "SENDERCODE"=#{SENDERCODE}
ORDER BY "SENDDATE" DESC, "SENDERCODE" ASC

SELECT SUM("TOTAL_SEND_CNT") AS "TOTAL_SEND_CNT", SUM("SEND_CNT") AS "SUCC_CNT", SUM("FAIL_CNT") AS "FAIL_CNT", SUM("REV_CNT") AS "RECV_CNT", SUM("READ_CNT") AS "READ_CNT" FROM t_push_msg_recv_master
AND "APPID" = #{APPID, jdbcType=VARCHAR}
AND "REGDATE" BETWEEN TO_DATE(#{FROM_DATE},'YYYY.MM.DD hh24:mi:ss') AND TO_DATE(#{TO_DATE},'YYYY.MM.DD hh24:mi:ss')
