mapper.select:
SELECT C."SEQNO" ,C."RESERVEDATE" ,C."APPID" ,C."CUID" ,C."SENDERSEQ" ,C."SENDERCODE" ,C."MESSAGE" ,C."REGDATE" FROM ( SELECT A."SEQNO ,TO_CHAR(A."RESERVEDATE", 'YYYY.MM.DD HH24:MI') AS "RESERVEDATE" ,A."APPID" ,A."CUID" ,A."SENDERSEQ" ,(SELECT "SENDERCODE" FROM "T_PUSH_SENDER_INFO" AS B WHERE A."SENDERSEQ" = B."SENDERSEQ") AS "SENDERCODE" ,A."MESSAGE" , TO_CHAR(A."REGDATE", 'YYYY.MM.DD HH24:MI') AS "REGDATE" FROM t_push_message AS A WHERE A."RESERVEDATE" != A."REGDATE" AND A."RESERVEDATE" > now() AND A."TYPE" = 'N' AND A."RESERVEDATE" BETWEEN TO_DATE(#{RESERVEDATE_S}, 'YYYY.MM.DD HH24MI') AND TO_DATE(#{RESERVEDATE_E}, 'YYYY.MM.DD HH24MI') AND A."APPID" = #{APPID}
AND A."SENDERSEQ" = #{SENDERSEQ}
ORDER BY "RESERVEDATE" ASC ) C INNER JOIN "T_PUSH_USER" AS D ON (C.CUID = D.CUID)
1 = 1
AND D."CNAME" = #{CNAME}
AND D."CUID" = #{CUID}
AND D."PHONENO" = #{PHONENO}
